    private void Clear2ButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Clear2ButtonActionPerformed
        // TODO add your handling code here:
        if(MainDisplay.getText().length() > 1)
            MainDisplay.setText(MainDisplay.getText().substring(0, MainDisplay.getText().length()-1));
        else 
            MainDisplay.setText("");
    }//GEN-LAST:event_Clear2ButtonActionPerformed

    private void ButtonEqualActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButtonEqualActionPerformed
        // TODO add your handling code here:
        float value1,value2;
        if(!MainDisplay.getText().isEmpty() && !SecondDisplay.getText().isEmpty() && SecondDisplay.getText().contains("+")){
            value1 = Float.parseFloat(SecondDisplay.getText().substring(0, SecondDisplay.getText().indexOf("+")));
            value2 = Float.parseFloat(MainDisplay.getText().substring(MainDisplay.getText().indexOf("+")+1, MainDisplay.getText().length()));
            SecondDisplay.setText(MainDisplay.getText());
            MainDisplay.setText(String.valueOf(value1 + value2));
        }else if(!MainDisplay.getText().isEmpty() && !SecondDisplay.getText().isEmpty() && SecondDisplay.getText().contains("-")){
            value1 = Float.parseFloat(SecondDisplay.getText().substring(0, SecondDisplay.getText().indexOf("-")));
            value2 = Float.parseFloat(MainDisplay.getText().substring(MainDisplay.getText().indexOf("-")+1, MainDisplay.getText().length()));
            SecondDisplay.setText(MainDisplay.getText());
            MainDisplay.setText(String.valueOf(value1 - value2));
        }else if(!MainDisplay.getText().isEmpty() && !SecondDisplay.getText().isEmpty() && SecondDisplay.getText().contains("*")){
            value1 = Float.parseFloat(SecondDisplay.getText().substring(0, SecondDisplay.getText().indexOf("*")));
            value2 = Float.parseFloat(MainDisplay.getText().substring(MainDisplay.getText().indexOf("*")+1, MainDisplay.getText().length()));
            SecondDisplay.setText(MainDisplay.getText());
            MainDisplay.setText(String.valueOf(value1 * value2));
        }else if(!MainDisplay.getText().isEmpty() && !SecondDisplay.getText().isEmpty() && SecondDisplay.getText().contains("/")){
            value1 = Float.parseFloat(SecondDisplay.getText().substring(0, SecondDisplay.getText().indexOf("/")));
            value2 = Float.parseFloat(MainDisplay.getText().substring(MainDisplay.getText().indexOf("/")+1, MainDisplay.getText().length()));
            SecondDisplay.setText(MainDisplay.getText());
            MainDisplay.setText(String.valueOf(value1 / value2));
        }else if(!MainDisplay.getText().isEmpty() && !SecondDisplay.getText().isEmpty() && SecondDisplay.getText().contains("%")){
            value1 = Float.parseFloat(SecondDisplay.getText().substring(0, SecondDisplay.getText().indexOf("%")));
            value2 = Float.parseFloat(MainDisplay.getText().substring(MainDisplay.getText().indexOf("%")+1, MainDisplay.getText().length()));
            SecondDisplay.setText(MainDisplay.getText());
            MainDisplay.setText(String.valueOf((value1 /100 )* value2));
        }
    }//GEN-LAST:event_ButtonEqualActionPerformed

