    public void setarNumLabe(char num){
        String texto = jTexTela.getText();
        texto = texto + num;
        jTexTela.setText(texto);
        
        
    }
    public void pegarNum1(){
        num1 = Double.parseDouble(jTexTela.getText());
        jTexTela.setText("");
        
    }
    public void pegarnum2(){
        num2 = Double.parseDouble(jTexTela.getText());
        jTexTela.setText("");
    }
    
